import { TOOL_ITEMS } from "../constants";
import rough from 'roughjs/bin/rough';

const gen = rough.generator();

export const createRoughElement = (id, x1, y1, x2, y2, { type }) => {
    console.log(`Creating element of type: ${type}`);  // Debugging line
    const element = {
        id,
        x1,
        y1,
        x2,
        y2,
    };
    switch (type) {
        case TOOL_ITEMS.LINE:
            element.roughEle = gen.line(x1, y1, x2, y2);
            return element;
        case TOOL_ITEMS.RECTANGLE:
            element.roughEle = gen.rectangle(x1, y1, x2 - x1, y2 - y1);
            return element;
        default:
            console.error(`Unsupported tool type: ${type}`); 
            throw new Error(`Type not recognized: ${type}`);
    }
};
