import React, { useReducer } from 'react';
import { TOOL_ITEMS } from "../constants";
import BoardContext from './board-context';
import rough from 'roughjs/bin/rough';

const gen = rough.generator();

const boardReducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_TOOL":
      return {
        ...state,
        activeToolItem: action.payload.tool,
      };
    case "DRAW_DOWN":
      const { clientX, clientY} = action.payload;
      const newEelement = {
        id: state.elements.length,
        x1: clientX,
        y1: clientY,
        x2: clientX,
        y2: clientY,
        roughEle: gen.line(clientX,clientY,clientX,clientY),
      };
      const prevElements = state.elements;
      return {
        ...state,
      elements: [...prevElements.elements, ]
      }
    default:
      return state;
  }
};

const initialBoardState = {
  activeToolItem: TOOL_ITEMS.LINE, 
  elements: [],
};

const BoardProvider = ({ children }) => {
  const [boardState, dispatchBoardAction] = useReducer(boardReducer, initialBoardState);

  const handleToolItemClick = (tool) => {
    dispatchBoardAction({
      type: "CHANGE_TOOL",
      payload: { tool },  // Correctly pass tool inside an object
    });
  };

  const boardMouseDownHandler = (event) => {
    const { clientX, clientY } = event;  // Destructure the event object correctly
    const roughEle = gen.line(clientX, clientY, clientX, clientY);
    dispatchBoardAction({
      type: "DRAW_DOWN",
      payload: {
        clientX,clientY,
      }
    });
  };
  

  const boardContextValue = {
    activeToolItem: boardState.activeToolItem,
    elements: boardState.elements,
    handleToolItemClick,
    boardMouseDownHandler,  // Use the corrected function name
  };
  

  return (
    <BoardContext.Provider value={boardContextValue}>
      {children}
    </BoardContext.Provider>
  );
};

export default BoardProvider;
