export const createElement = (id, x1, y1, x2, y2, options) => {
  return {
    id,
    x1,
    y1,
    x2,
    y2,
    ...options,
    points: [],
    path: new Path2D(),
    sets: [] // Initialize sets to an empty array
  };
};

export const getSvgPathFromStroke = (stroke) => {
  if (!stroke) return '';
  return stroke
    .map(([x, y], i) => (i === 0 ? `M ${x} ${y}` : `L ${x} ${y}`))
    .join(' ');
};

export const isPointNearElement = (element, x, y) => {
  // Placeholder for a real implementation
  const distance = Math.sqrt((x - element.x1) ** 2 + (y - element.y1) ** 2);
  return distance < 10; // Example threshold
};
