import React, { useReducer } from 'react';
import { BOARD_ACTIONS, TOOL_ACTION_TYPES, TOOL_ITEMS } from "../constants";
import BoardContext from './board-context';
import rough from 'roughjs/bin/rough';
import { createRoughElement } from '../utils/elements';

const gen = rough.generator();

const boardReducer = (state, action) => {
  switch (action.type) {
    case BOARD_ACTIONS.CHANGE_TOOL:
      return {
        ...state,
        activeToolItem: action.payload.tool,
      };
    case BOARD_ACTIONS.DRAW_DOWN:
      const newElement = createRoughElement(state.elements.length, clientX, clientY,clientX,clientY,{type: state.activeToolItem})
      return {
        ...state,
        toolActionType:TOOL_ACTION_TYPES.DRAWING,
        elements: [...state.elements, newElement], // Add new element to the array
      };
    case BOARD_ACTIONS.DRAW_MOVE:
      const updatedElements = state.elements.map((ele, index) => {
        if (index === state.elements.length - 1) {
          return {
            ...ele,
            x2: action.payload.clientX,
            y2: action.payload.clientY,
            roughEle: gen.line(ele.x1, ele.y1, action.payload.clientX, action.payload.clientY),
          };
        }
        return ele;
      });
      return {
        ...state,
        elements: updatedElements,
      };
    case BOARD_ACTIONS.DRAW_UP : {
      return {
        ...state,
        toolActionType: TOOL_ACTION_TYPES.NONE,
      }
    }
    default:
      return state;
  }
};

const initialBoardState = {
  activeToolItem: TOOL_ITEMS.LINE,
  toolActionType: TOOL_ACTION_TYPES.NONE,
  elements: [],
};

const BoardProvider = ({ children }) => {
  const [boardState, dispatchBoardAction] = useReducer(boardReducer, initialBoardState);

  const changeToolHandler = (tool) => {
    dispatchBoardAction({
      type: BOARD_ACTIONS.CHANGE_TOOL,
      payload: { tool },
    });
  };

  const boardMouseDownHandler = (event) => {
    const { clientX, clientY } = event;
    dispatchBoardAction({
      type: BOARD_ACTIONS.DRAW_DOWN,
      payload: { clientX, clientY },
    });
  };

  const boardMouseMoveHandler = (event) => {
    const { clientX, clientY } = event;
    dispatchBoardAction({
      type: BOARD_ACTIONS.DRAW_MOVE,
      payload: { clientX, clientY },
    });
  };

  const boardMouseUpHandler = () => {
    dispatchBoardAction({
      type: BOARD_ACTIONS.DRAW_UP,
    });
  };

  const boardContextValue = {
    activeToolItem: boardState.activeToolItem,
    elements: boardState.elements,
    toolActionType: boardState.toolActionType,
    changeToolHandler,
    boardMouseDownHandler,
    boardMouseMoveHandler,
    boardMouseUpHandler,
  };

  return (
    <BoardContext.Provider value={boardContextValue}>
      {children}
    </BoardContext.Provider>
  );
};

export default BoardProvider;
