import { useContext, useEffect, useRef } from "react";
import rough from "roughjs/bundled/rough.cjs";
import boardContext from "../../Store/board-context";

function Board() {
  const canvasRef = useRef();
  const { elements, boardMouseDownHandler, boardMouseMoveHandler, toolActionType} = useContext(boardContext);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const context = canvas.getContext("2d");
    const roughCanvas = rough.canvas(canvas);

    elements.forEach(element => {
      // Draw elements using the roughCanvas instance
      roughCanvas.draw(element.roughEle);
    });

    return () => {
      context.clearRect(0, 0, canvas.width, canvas.height);
    };
  }, [elements]);

  const handleBoardMouseDown = (event) => {
    boardMouseDownHandler(event);
  }

  const handleMouseMove = (event) => {
    if(toolActionType===TOOL)
    boardMouseMoveHandler(event);
  }

  return (
    <canvas ref={canvasRef} onMouseDown={handleBoardMouseDown} onMouseMove = {handleMouseMove}/>
  );
}

export default Board;
