// board-context.js
import { createContext } from 'react';

const boardContext = createContext({
  activeToolItem: "",  // Default tool, can be empty or set to a default value
  toolActionType: "",  // Provide a default value, e.g., "" or a specific action type
  elements: [],
  boardMouseDownHandler: () => {},
  changeToolHandler: () => {},
  boardMouseMoveHandler: () => {}
});

export default boardContext;
