  const boardContextValue = {
    activeToolItem,
  }
  return (
    <boardContext.Provider value={{activeToolItem: TOOL_ITEMS.LINE, elements:[]}}>{
        children
    }</boardContext.Provider>
  )
}

export default BoardProvider
